import re

_jwt = re.compile(r'eyJ[A-Za-z0-9_-]*\.eyJ[A-Za-z0-9_-]*\.[A-Za-z0-9_-]*')

def find_positions(text):
    positions = ["{},{}".format(m.start(), m.end()) for m in _jwt.finditer(text)]
    return ";".join(positions)

output = find_positions(input)

